import React, { useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

import Loading from '../Loading/loading';
import { useNavigate } from "react-router-dom";
import Moment from 'moment';
import { fetchAuthors } from '../../redux/actions/author/authorActions';
import Sidebar from '../admin/Sidebar';
import Loader from '../layout/Loader';
import { MDBDataTable } from 'mdbreact';

const Authors = () => {
    //Fetch books
    const dispatch = useDispatch();
    const history = useNavigate();
    useEffect(() => {
        dispatch(fetchAuthors());
    }, [dispatch]);
    const authorsList = useSelector(state => state.authorsList);
    const { authors, loading } = authorsList;
    // End of fetch authors
    const setAuthors = () => {
        const data = {
            columns: [
                {
                    label: 'Author ID',
                    field: 'id',
                    sort: 'asc'
                },
                {
                    label: 'Author Name',
                    field: 'name',
                    sort: 'asc'
                },
                {
                    label: 'Birthday',
                    field: 'date',
                    sort: 'asc'
                },
                {
                    label: 'Actions',
                    field: 'actions',
                },
            ],
            rows: []
        }
        //
        authors.forEach(author => {
            data.rows.push({
                id: author._id,
                name: author.name,
                email: author.year,

                actions: <Fragment>
                    <Link to='#'className="btn btn-primary py-1 px-2">
                        <i className="fa fa-pencil"></i>
                    </Link>
                    <button className="btn btn-danger py-1 px-2 ml-2" >
                        <i className="fa fa-trash"></i>
                    </button>
                </Fragment>
            })
        })

        return (
            <Fragment>
                <div className="row mt-5">
                    <div className="col-12 col-md-2 mt-4">
                        <Sidebar />
                    </div>

                    <div className="col-12 col-md-10 mt-5">
                        <Fragment>
                            <h1 className="my-5">All Authors</h1>

                            {loading ? <Loader /> : (
                                <MDBDataTable
                                    data={setAuthors()}
                                    className="px-3"
                                    bordered
                                    striped
                                    hover
                                />
                            )}

                        </Fragment>
                    </div>
                </div>

            </Fragment>
        );
    };
};

export default Authors;
