import React, { Fragment, useEffect } from 'react';
import './Home.css';
import bookpg from '../../assets/img/book.jpg';
// import videoSource from '../../assets/books.mp4';
import videoSources from '../../assets/book.mp4';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import Books from '../Books/FetchBook';
import { fetchBooks } from '../../redux/actions/books/bookActions';
import { useNavigate } from "react-router-dom";
import Loading from '../Loading/loading';

const Home = () => {

    const dispatch = useDispatch();
    const history = useNavigate();
    const { books, loading, error } = useSelector((state) => state.booksList);

    useEffect(() => {
        if (error) {
            return alert(error);
        }
        dispatch(fetchBooks());
    }, [dispatch, error, alert]);

    return (
        <Fragment>
            <Fragment>
                <div className='Container'>
                    <video autoPlay='autoplay' loop='loop' muted className='Video'>
                        <source src={videoSources} type='video/mp4' />
                        Your browser does not support the video tag.
                    </video>
                    <div className='Content'>
                        <div className='SubContent'>
                            <h1>Book Catolog</h1>
                            <p>Manage your Books with Ease</p>
                            <button type='button' className='btn btn-outline-dark'>
                                <Link to='/register'>Get started</Link>
                            </button>
                            <img src={bookpg} alt='profile' />
                        </div>
                    </div>
                </div>


            </Fragment>
            <Fragment>
                {loading && <Loading />}
                {books !== undefined && books.length === 0 ? (
                    'No'
                ) : (
                    <section id="products" className="container mt-5">
                        <div className="row">
                            <div className="col-6 col-md-9">
                                <div className="row">
                                    <div class={`col-sm-12 col-md-6 col-lg-4 my-3`}>
                                        <div
                                            className="eg-product-carde-alpha shadow-lg "
                                            style={{ borderRadius: "20px" }}
                                        >
                                            <div className="eg-porduct-thumb">
                                                <Link to={`/bookDetail/${books._id}`}  >
                                                    <img
                                                        className="p-3"
                                                        src={books.book_img[0].url}
                                                        alt="Book Img"
                                                        style={{ width: "100%" }}
                                                    />
                                                </Link>
                                            </div>
                                            <div className="eg-porduct-body mt-2">
                                                <h5 className="eg-product-title">
                                                    <Link to={`/bookDetail/${books._id}`}>{books.book_name}</Link>
                                                </h5>
                                                <div className="eg-product-card-price">
                                                    <ins>
                                                        <span className="price-amount">
                                                            <bdi>VND {books.unitPrice}</bdi>
                                                        </span>
                                                    </ins>
                                                </div>

                                                <div className="product-card-bottom">
                                                    
                                                    <div className="product-add-btn">
                                                        <Link to={`/bookDetail/${books._id}`}>
                                                            View Details <i className="fa fa-plus"></i>
                                                        </Link>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                )}
            </Fragment>

        </Fragment>
    );
};

export default Home;
