import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchBooks, deleteBook } from '../../redux/actions/books/bookActions';
import Loading from '../Loading/loading';
import { useNavigate } from "react-router-dom";
import Moment from 'moment';

const Books = () => {
    //Fetch books
    const dispatch = useDispatch();
    const history = useNavigate();
    useEffect(() => {
        dispatch(fetchBooks());
    }, [dispatch]);
    const bookslist = useSelector(state => state.booksList);
    const { books, loading } = bookslist;
    // End of fetch books
    //Delete book handler
    const handlerDeleteBook = id => {
        dispatch(deleteBook(id));
        alert('Delete Success');
        history('/books');
        window.location.replace('/books');
    };
    return (
        <div>
            {loading && <Loading />}
            {books !== undefined && books.length === 0 ? (
                'No'
            ) : (
                <div className='row'>
                    <div className='col'>
                        <table className='table table-hover'>
                            <thead>
                                <tr>
                                    <th scope='col'>Book Name</th>
                                    <th scope='col'>publishedDate</th>
                                    <th scope='col'>genres</th>
                                    {/* <th scope='col'>Author</th> */}
                                    <th scope='col'>unitPrice</th>
                                    <th scope='col'>Book image</th>
                                    <th scope='col'>Action</th>
                                    <th scope='col'>Action</th>

                                </tr>
                            </thead>
                            <tbody>
                                {books &&
                                    books.map(book => {
                                        return (
                                            <><tr key={book._id}>
                                                <th scope='row'>{book.book_name}</th>
                                                <td>{Moment(book.publishedDate).calendar()}</td>
                                                <td>{book.genres}</td>
                                                {/* <td>{book.author}</td> */}
                                                <td>{book.unitPrice.toLocaleString('it-IT', { style: 'currency', currency: 'VND' })}</td>
                                                <td key={book.book_img.public_id}>

                                                    <img src={book.book_img.url} style={{ height: '350px' }} />
                                                </td>
                                                <td>
                                                    <button
                                                        type="button"
                                                        onClick={() => handlerDeleteBook(book._id)}
                                                        className="btn btn-danger"
                                                        style={{ color: 'white', cursor: 'pointer' }}>Delete</button>
                                                </td>
                                                <td>
                                                    <Link to={`/bookdetail/${book && book._id}`}>
                                                        <button
                                                            type='button'
                                                            className='btn btn-success'
                                                            style={{
                                                                color: 'yellow',
                                                                cursor: 'pointer',
                                                            }}>Details</button>
                                                    </Link>
                                                </td>

                                            </tr><section id="products" className="container mt-5">
                                                    <div className="row">
                                                        <div className="col-6 col-md-9">
                                                            <div className="row">
                                                                <div class={`col-sm-12 col-md-6 col-lg-4 my-3`}>
                                                                    <div
                                                                        className="eg-product-carde-alpha shadow-lg "
                                                                        style={{ borderRadius: "20px" }}
                                                                    >
                                                                        <div className="eg-porduct-thumb">
                                                                            <Link to={`/bookDetail/${books._id}`}>
                                                                                <img
                                                                                    className="p-3"
                                                                                    src={books.book_img[0].url}
                                                                                    alt="Book Img"
                                                                                    style={{ width: "100%" }} />
                                                                            </Link>
                                                                        </div>
                                                                        <div className="eg-porduct-body mt-2">
                                                                            <h5 className="eg-product-title">
                                                                                <Link to={`/bookDetail/${books._id}`}>{books.book_name}</Link>
                                                                            </h5>
                                                                            <div className="eg-product-card-price">
                                                                                <ins>
                                                                                    <span className="price-amount">
                                                                                        <bdi>VND {books.unitPrice}</bdi>
                                                                                    </span>
                                                                                </ins>
                                                                            </div>

                                                                            <div className="product-card-bottom">

                                                                                <div className="product-add-btn">
                                                                                    <Link to={`/bookDetail/${books._id}`}>
                                                                                        View Details <i className="fa fa-plus"></i>
                                                                                    </Link>
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </section></>
                                        );
                                    })}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Books;
