import { ADD_AUTHOR_FAIL, ADD_AUTHOR_REQUEST, ADD_AUTHOR_SUCCESS, CLEAR_ERRORS, FETCH_AUTHOR_FAIL, FETCH_AUTHOR_REQUEST, FETCH_AUTHOR_SUCCESS, NEW_AUTHOR_RESET } from "../../actions/actionsTypes";

const authorListReducer = (state = [], action) => {
  switch (action.type) {
    case FETCH_AUTHOR_REQUEST:
      return {
        loading: true,
      };
    case FETCH_AUTHOR_SUCCESS:
      return {
        authors: action.payload,
        loading: false,
      };
    case FETCH_AUTHOR_FAIL:
      return {
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

const addAuthorReducer = (state = {}, action) => {
  switch (action.type) {
    case ADD_AUTHOR_REQUEST:
      return {
        loading: true,
      };
    case ADD_AUTHOR_SUCCESS:
      return {
        loading: false,
        authors: action.payload,

      };
    case ADD_AUTHOR_FAIL:
      return {
        loading: false,
        error: action.payload,
      };
    case NEW_AUTHOR_RESET:
      return {
        ...state,
        success: false,
      };
    case CLEAR_ERRORS:
      return {
        ...state,
        error: null,
      };
    default:
      return state;
  }
};

export { authorListReducer, addAuthorReducer };
